import React, { useEffect, useState } from "react";
import userImage from "../assets/user.png"; // Add a default user image to the 'assets' folder
import { auth, db } from "../firebase";
import { doc, getDoc } from "firebase/firestore";
import { useNavigate } from "react-router-dom";
import './AddItem.css';

const UserDashboard = () => {
  const [userName, setUserName] = useState("User");
  const [orderItems, setOrderItems] = useState([]);
  const [tableNumber, setTableNumber] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      const user = auth.currentUser;
      if (user) {
        const userDoc = await getDoc(doc(db, "users", user.uid));
        if (userDoc.exists()) {
          setUserName(userDoc.data().name);
        }
      }
    };

    fetchUserData();
  }, []);

  const handleLogout = () => {
    auth.signOut();
    navigate("/login");
  };

  const handleAddItem = (item) => {
    setOrderItems((prevItems) => [...prevItems, item]);
  };

  const handlePlaceOrder = () => {
    if (!tableNumber) {
      alert("Please select a table number.");
      return;
    }

    const orderData = {
      tableNumber,
      items: orderItems,
      total: calculateTotal(),
    };

    // Send orderData to admin dashboard (Firebase or other storage logic here)

    alert("Order placed successfully!");
    setOrderItems([]);
    setTableNumber("");
  };

  const calculateTotal = () => {
    return orderItems.reduce((total, item) => total + item.price, 0).toFixed(2);
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      {/* Ribbon Section */}
      <div style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        padding: "10px",
        backgroundColor: "#f0f0f0",
        border: "1px solid #ddd",
        borderRadius: "10px",
        marginBottom: "20px",
      }}>
        <div style={{ display: "flex", alignItems: "center" }}>
          <img
            src={userImage}
            alt="User"
            style={{ height: "50px", width: "50px", borderRadius: "50%", marginRight: "10px" }}
          />
          <h2>Welcome, {userName}!</h2>
        </div>
        <button onClick={handleLogout} style={{ padding: "5px 10px", cursor: "pointer" }}>Logout</button>
      </div>

      {/* Menu Card Container */}
      <div style={{
        margin: "20px",
        padding: "20px",
        border: "1px solid #ddd",
        borderRadius: "10px",
        backgroundColor: "#fafafa",
      }}>
        <div style={{
          display: "flex",
          justifyContent: "space-between",
          gap: "10px",
        }}>


        

          {/* Starter Section */}
          <div style={{ flex: 1, padding: "20px", border: "1px solid #ccc", borderRadius: "8px", backgroundColor: "#fff" }}>
            <h3>Starters</h3>
            <div style={{ margin: "10px 0" }}>
              <img src="/starter1.jpg" alt="Starter 1" style={{ width: "100px", height: "100px", borderRadius: "8px" }} />
