import React, { useEffect, useState } from "react";
import userImage from "../assets/user.png"; // Add a default user image to the 'assets' folder
import { auth, db } from "../firebase";
import { doc, getDoc, addDoc, collection } from "firebase/firestore";
import { useNavigate } from "react-router-dom";

const UserDashboard = () => {
  const [userName, setUserName] = useState("User");
  const [orderItems, setOrderItems] = useState([]);
  const [tableNumber, setTableNumber] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      const user = auth.currentUser;
      if (user) {
        const userDoc = await getDoc(doc(db, "users", user.uid));
        if (userDoc.exists()) {
          setUserName(userDoc.data().name);
        }
      }
    };

    fetchUserData();
  }, []);

  const handleLogout = () => {
    auth.signOut();
    navigate("/login");
  };

  const handleAddItem = (item) => {
    setOrderItems((prevItems) => [...prevItems, item]);
  };

  const calculateTotal = () => {
    return orderItems.reduce((total, item) => total + item.price, 0).toFixed(2);
  };

  const handlePlaceOrder = async () => {
    if (!tableNumber) {
      alert("Please select a table number.");
      return;
    }

    const confirmOrder = window.confirm(
      `Are you sure you want to place the order?\nTable: ${tableNumber}\nTotal: ₹${calculateTotal()}`
    );

    if (!confirmOrder) {
      return;
    }

    try {
      await addDoc(collection(db, "orders"), {
        tableNumber,
        items: orderItems,
        total: calculateTotal(),
        placedBy: userName,
        timestamp: new Date(),
      });
      alert("Order placed successfully!");
      setOrderItems([]);
      setTableNumber("");
    } catch (error) {
      console.error("Error placing order: ", error);
      alert("Failed to place the order. Please try again.");
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      {/* ...UserDashboard content */}
      <button
        onClick={handlePlaceOrder}
        style={{
          padding: "10px 20px",
          marginTop: "10px",
          backgroundColor: "#28a745",
          color: "white",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        Place Order
      </button>
    </div>
  );
};

export default UserDashboard;
