import React, { useEffect, useState } from "react";
import userImage from "../assets/user.png"; // Add a default user image to the 'assets' folder
import { auth, db } from "../firebase";
import { doc, getDoc, addDoc, collection } from "firebase/firestore";
import { useNavigate } from "react-router-dom";

const UserDashboard = () => {
  const [userName, setUserName] = useState("User");
  const [orderItems, setOrderItems] = useState([]);
  const [tableNumber, setTableNumber] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      const user = auth.currentUser;
      if (user) {
        const userDoc = await getDoc(doc(db, "users", user.uid));
        if (userDoc.exists()) {
          setUserName(userDoc.data().name);
        }
      }
    };

    fetchUserData();
  }, []);

  const handleLogout = () => {
    auth.signOut();
    navigate("/login");
  };

  const handleAddItem = (item) => {
    setOrderItems((prevItems) => [...prevItems, item]);
  };

  const calculateTotal = () => {
    return orderItems.reduce((total, item) => total + item.price, 0).toFixed(2);
  };

  const handlePlaceOrder = async () => {
    if (!tableNumber) {
      alert("Please select a table number.");
      return;
    }
    try {
      await addDoc(collection(db, "orders"), {
        tableNumber,
        items: orderItems,
        total: calculateTotal(),
        placedBy: userName,
        timestamp: new Date(),
      });
      alert("Order placed successfully!");
      setOrderItems([]);
      setTableNumber("");
    } catch (error) {
      console.error("Error placing order: ", error);
      alert("Failed to place the order. Please try again.");
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <div style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        padding: "10px",
        backgroundColor: "#f0f0f0",
        border: "1px solid #ddd",
        borderRadius: "10px",
        marginBottom: "20px",
      }}>
        <div style={{ display: "flex", alignItems: "center" }}>
          <img
            src={userImage}
            alt="User"
            style={{ height: "50px", width: "50px", borderRadius: "50%", marginRight: "10px" }}
          />
          <h2>Welcome, {userName}!</h2>
        </div>
        <button onClick={handleLogout} style={{ padding: "5px 10px", cursor: "pointer" }}>Logout</button>
      </div>

      {/* Menu Section */}
      <div style={{
        margin: "20px",
        padding: "20px",
        border: "1px solid #ddd",
        borderRadius: "10px",
        backgroundColor: "#fafafa",
      }}>
        {/* Menu content remains the same */}
      </div>

      {/* Total Order Section */}
      <div style={{
        margin: "20px",
        padding: "20px",
        border: "1px solid #ddd",
        borderRadius: "10px",
        backgroundColor: "#f9f9f9",
      }}>
        <h3>Total Order</h3>
        {orderItems.length === 0 ? (
          <p>No items added yet.</p>
        ) : (
          <>
            <ul>
              {orderItems.map((item, index) => (
                <li key={index}>{item.name} - ₹{item.price.toFixed(2)}</li>
              ))}
            </ul>
            <h4>Total: ₹{calculateTotal()}</h4>
            <hr />
            <div style={{ margin: "10px 0" }}>
              <label>
                Select Table Number:
                <select
                  value={tableNumber}
                  onChange={(e) => setTableNumber(e.target.value)}
                  style={{ marginLeft: "10px", padding: "5px" }}
                  required
                >
                  <option value="">--Select--</option>
                  {Array.from({ length: 20 }, (_, i) => (
                    <option key={i + 1} value={i + 1}>
                      Table {i + 1}
                    </option>
                  ))}
                </select>
              </label>
            </div>
            <button
              onClick={handlePlaceOrder}
              style={{
                padding: "10px 20px",
                marginTop: "10px",
                backgroundColor: "#28a745",
                color: "white",
                border: "none",
                borderRadius: "5px",
                cursor: "pointer",
              }}
            >
              Place Order
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default UserDashboard;
